mapper.select:
select userid
from user
where username = #{username} and useraddress = #{useraddress} and usernumber = #{usernumber}

select *
from user
where username = #{username} and userid=#{userid} and useraddress = #{useraddress} and usernumber = #{usernumber}

select userpw
from user
where userid = #{userid}

select count(userid)
from user
where userid = #{userid}

select *
from user
where userid = #{userid} and userpw = #{userpw}

select *
from user
order by userid desc;

mapper.update:
update user
userpw = #{userpw}, useraddress = #{useraddress}, usernumber = #{usernumber},
where userid = #{userid}
